const { ObjectId } = mongoose.Types;

       const checkObjectId = (ctx, next) => {
    const { id } = ctx.parmas;
    if (!ObjectId.isValid(id)) {
        ctx.status = 400; // Bad Request
        return;
    }
    return next();
};

// 포스트 작성 POST /api/posts
// { title, body }
       const write = async ctx => {
    const { title, body, tags } = ctx.request.body;
    const post = new Post({
        title,
        body,
        tags,
    });
    try {
        await post.save();
        ctx.body = post;
    } catch (e) {
        ctx.throw(500, e);
    }
};

       const list = async ctx => {
    try {
        const posts = await Post.find().exec();
        ctx.body = posts;
    } catch (e) {
        ctx.throw(500, e);
    }
   
}

// 특정 포스트 조회
// GET /api/posts/:id
       const read = async ctx => {
    const { id } = ctx.params;
    try {
        const post = await Post.findById(id).exec();
        if (!post) {
            ctx.status = 404; // not found
            return;
        }
        ctx.body = post;
    }catch (e) {
        ctx.throw(500, e);
    }
};

// 특정 포스트 제거
// DELETE /api/posts/:id
       const remove = async ctx => {
    const { id } = ctx.params;
    try {
        await Post.findByIdAndRemove(id).exec();
        ctx.status = 204; // No Content (성공했지만 응답할 데이터는 없음)
    }catch(e) {
        ctx.throw(500, e)
    }
}

// 포스트 수정(교체)
// PUT /api/posts/:id
//{ title, body }
       const replace = async ctx => {
    const { id } = ctx.params;
    try {
        const post = await Post.findByIdAndUpdate(id, ctx.request.body, {
            new: true, // 이값을 설정하면 업데이트된 데이터가 반환
            // false일땐 업데이트되기 전의 데이터를 반환
        }).exec();
        if(!post) {
            ctx.status = 404;
            return;
        }
        ctx.body = post;
    }catch(e) {
        ctx.throw(500, e);
    }
};

// 포스트 수정(특정 필드 변경)
// PATCH /api/posts/:id
// { title, body }
       const update = async ctx => {
    const { id } = ctx.params;
    try {
        const post = await Post.findByIdAndUpdate(id, ctx.request.body, {
            new: true, // 이값을 설정하면 업데이트된 데이터가 반환
            // false일땐 업데이트되기 전의 데이터를 반환
        }).exec();
        if(!post) {
            ctx.status = 404;
            return;
        }
        ctx.body = post;
    }catch(e) {
        ctx.throw(500, e);
    }
}


